#include "include.h"
#include "../src/word_search_st.h"
#include "../src/word_search_mt.h"

// Tests the single-threaded version of Word Search on basic input.
TEST(WordSearchST_test, BasicTest) {
    WordSearch word_search;
    word_search.SetStrategy(new WordSearchST());

    std::vector<std::vector<char>> board =
            {{'A','B','C','E'},
             {'S','F','C','S'},
             {'A','D','E','E'}};
    std::string word = "ABCCED";

    ASSERT_EQ(word_search.exist(board, word), true);
}


// Tests the multi-threaded version of Word Search on simple input.
TEST(WordSearchMT_test, SimpleTest) {
    WordSearch word_search;
    word_search.SetStrategy(new WordSearchMT());

    std::vector<std::vector<char>> board = {{'a','a'}};
    std::string word = "a";

    ASSERT_EQ(word_search.exist(board, word), true);
}

// Tests the multi-threaded version of Word Search on basic input.
TEST(WordSearchMT_test, BasicTest1) {
    WordSearch word_search;
    word_search.SetStrategy(new WordSearchMT());

    std::vector<std::vector<char>> board = {{'C','A','A'},{'A','A','A'},{'B','C','D'}};
    std::string word = "AAB";

    ASSERT_EQ(word_search.exist(board, word), true);
}

